body { margin: 0px; padding: 0px; font-family: "PF DinDisplay Pro", sans-serif; background-color: rgb(255, 255, 255); height: 100%; line-height: 1; position: relative; -webkit-font-smoothing: antialiased; font-size: 1.6rem !important; }
.overflow-hidden { overflow: hidden; }
a, abbr, acronym, address, applet, article, aside, audio, b, big, blockquote, body, canvas, caption, center, cite, code, dd, del, details, dfn, div, dl, dt, em, embed, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, i, iframe, img, ins, kbd, label, legend, li, mark, menu, nav, object, ol, output, p, pre, q, ruby, s, samp, section, small, span, strike, strong, sub, summary, sup, table, tbody, td, tfoot, th, thead, time, tr, tt, u, ul, var, video { margin: 0px; padding: 0px; border: 0px; font-size: 100%; }
article, aside, details, figcaption, figure, footer, header, hgroup, main, menu, nav, section { display: block; }
blockquote, q { quotes: none; }
blockquote::after, blockquote::before, q::after, q::before { content: none; }
table { border-collapse: collapse; border-width: 0px; padding: 0px; margin: 0px; }
html { height: 100%; text-size-adjust: none; font-size: 62.5%; }
input, textarea { color: rgb(39, 39, 39); font-family: "PF DinDisplay Pro", sans-serif; outline: none; border-radius: 0px; appearance: none; }
button, input[type="button"], input[type="submit"] { cursor: pointer; }
form, td { margin: 0px; padding: 0px; }
a, button { color: rgb(192, 172, 131); transition: 0.3s linear; text-decoration: none; outline: none; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); }
a, button, div, span { outline: none !important; }
button, input[type="button"], input[type="submit"] { outline: none; padding: 0px; appearance: none; }
*, ::after, ::before { box-sizing: border-box; }
li, ol, ul { list-style-type: none; }
img { display: block; max-width: 100%; height: auto; }
.irs { user-select: none; }
.irs, .irs-line { position: relative; display: block; }
.irs-line { overflow: hidden; outline: none !important; }
.irs-line-left, .irs-line-mid, .irs-line-right { position: absolute; display: block; top: 0px; }
.irs-line-left { left: 0px; width: 11%; }
.irs-line-mid { left: 9%; width: 82%; }
.irs-line-right { right: 0px; width: 11%; }
.irs-bar { width: 0px; }
.irs-bar, .irs-bar-edge { position: absolute; display: block; left: 0px; }
.irs-bar-edge { top: 0px; }
.irs-shadow { position: absolute; display: none; left: 0px; width: 0px; }
.irs-slider { position: absolute; display: block; cursor: default; z-index: 1; }
.irs-slider.type_last { z-index: 2; }
.irs-min { left: 0px; }
.irs-max, .irs-min { position: absolute; display: block; cursor: default; }
.irs-max { right: 0px; }
.irs-from, .irs-single, .irs-to { position: absolute; display: block; top: 0px; left: 0px; cursor: default; white-space: nowrap; }
.irs-grid { position: absolute; display: none; bottom: 0px; left: 0px; width: 100%; height: 20px; }
.irs-with-grid .irs-grid { display: block; }
.irs-grid-pol { position: absolute; top: 0px; left: 0px; width: 1px; height: 8px; background: rgb(0, 0, 0); }
.irs-grid-pol.small { height: 4px; }
.irs-grid-text { position: absolute; bottom: 0px; left: 0px; white-space: nowrap; text-align: center; font-size: 9px; line-height: 9px; padding: 0px 3px; color: rgb(0, 0, 0); }
.irs-disable-mask { position: absolute; display: block; top: 0px; left: -1%; width: 102%; height: 100%; cursor: default; background: transparent; z-index: 2; }
.lt-ie9 .irs-disable-mask { background: rgb(0, 0, 0); cursor: not-allowed; }
.irs-disabled { opacity: 0.4; }
.irs-hidden-input { overflow: hidden; position: absolute !important; display: block !important; top: 0px !important; left: 0px !important; width: 0px !important; height: 0px !important; font-size: 0px !important; line-height: 0 !important; padding: 0px !important; margin: 0px !important; outline: none !important; z-index: -9999 !important; background: none !important; border-style: solid !important; border-color: transparent !important; }
.jq-checkbox-own { display: block; position: relative; cursor: pointer; user-select: none; border: 1px solid rgb(211, 211, 211); flex-shrink: 0; padding-left: 13px; width: 15px; height: 15px; margin-right: 5px; }
.jq-checkbox-own.checked + .label-text, .jq-checkbox-own.checked + .text { font-weight: 500; }
.jq-checkbox-own input { position: absolute; opacity: 0; cursor: pointer; height: 0px; width: 0px; }
.jq-checkbox__div-own { position: absolute; height: 6.5px; width: 7px; top: 50%; left: 50%; transform: translate(-50%, -50%); background-size: contain; }
.jq-checkbox-own input:checked ~ .jq-checkbox__div-own { background: url("../img/icon-check.svg") 50% center / contain no-repeat; }
.jq-checkbox__div-own::after { content: ""; position: absolute; display: none; }
.jq-checkbox__div-own input:checked ~ .jq-checkbox__div-own::after { display: block; }
.list-checkbox__label { -webkit-tap-highlight-color: transparent; }
.list-checkbox__label .jq-checkbox-own { width: 24px; height: 24px; padding-left: 22px; margin-right: 9px; }
.list-checkbox .jq-checkbox__div-own { height: 100%; width: 100%; }
.jq-checkbox-own .jq-checkbox__div-own::after { left: 9px; top: 5px; width: 5px; height: 10px; border-style: solid; border-color: rgb(255, 255, 255); border-image: initial; border-width: 0px 3px 3px 0px; transform: rotate(45deg); }
.filter__list .jq-checkbox-own ~ .text { font-size: 1.6rem; }
.list-checkbox__label .jq-checkbox-own input:checked ~ .jq-checkbox__div-own { background-size: auto; }
@media (max-width: 767px) {
  .list-checkbox__label .jq-checkbox-own { margin-right: 9px; width: 36px; height: 36px; padding-left: 34px; margin-bottom: 5px; }
  .jq-checkbox-own { margin-right: 9px; width: 36px; height: 36px; padding-left: 34px; margin-bottom: 5px; }
  .jq-checkbox__div-own { top: 0px; left: 0px; transform: none; width: 100%; height: 100%; background-size: auto; }
  .jq-checkbox-own input:checked ~ .jq-checkbox__div-own { background-size: auto; }
  .filter__checkbox .list-checkbox__label .label-text { margin-top: 0px; font-size: 1.6rem; line-height: normal; padding-top: 10px; margin-bottom: 5px; }
  .filter__list .jq-checkbox-own ~ .text { padding-top: 10px; margin-bottom: 5px; }
}
.jq-checkbox, .jq-radio { position: relative; display: inline-block; overflow: hidden; user-select: none; }
.jq-checkbox input, .jq-radio input { position: absolute; z-index: -1; margin: 0px; padding: 0px; opacity: 0; }
.jq-file { position: relative; display: inline-block; overflow: hidden; }
.jq-file input { position: absolute; top: 0px; right: 0px; height: auto; margin: 0px; padding: 0px; opacity: 0; font-size: 100px; line-height: 1em; }
.jq-file__name { overflow: hidden; white-space: nowrap; text-overflow: ellipsis; max-width: 210px; }
.jq-select-multiple, .jq-selectbox { position: relative; display: inline-block; }
.jq-select-multiple select, .jq-selectbox select { position: absolute; top: 0px; left: 0px; width: 100%; height: 100%; margin: 0px; padding: 0px; opacity: 0; }
.jq-select-multiple li, .jq-selectbox li { user-select: none; white-space: nowrap; }
.jq-selectbox { z-index: 10; }
.jq-selectbox__select-text { overflow: hidden; user-select: none; white-space: nowrap; text-overflow: ellipsis; }
.jq-selectbox__dropdown { position: absolute; }
.jq-selectbox__search input { appearance: textfield; }
.jq-selectbox__search input::-webkit-search-cancel-button, .jq-selectbox__search input::-webkit-search-decoration { appearance: none; }
.jq-selectbox__dropdown ul { overflow: hidden auto; list-style: none; }
.jq-select-multiple ul, .jq-selectbox__dropdown ul { position: relative; }
.jq-select-multiple ul { overflow-x: hidden; }
.jq-number { display: inline-block; }
.jq-number__field input { appearance: textfield; text-align: left; }
.jq-number__field input::-webkit-inner-spin-button, .jq-number__field input::-webkit-outer-spin-button { margin: 0px; appearance: none; }
.mCustomScrollbar { touch-action: pinch-zoom; }
.mCustomScrollBox { position: relative; overflow: hidden; height: 100%; max-width: 100%; outline: none; direction: ltr; }
.mCSB_container { overflow: hidden; width: auto; height: auto; }
