:root { --f-spinner-width: 36px; --f-spinner-height: 36px; --f-spinner-color-1: rgba(0, 0, 0, 0.1); --f-spinner-color-2: rgba(17, 24, 28, 0.8); --f-spinner-stroke: 2.75; }
